import React, { useState } from "react";
import Box from "./Box";
import NewBoxForm from "./NewBoxForm";

function BoxList(){
    const [boxes, setBoxes] = useState([{backgroundColor: "red", width: "100px", height: "100px"}]);

    const addBox = box => {
        const newBox = {...box};
        setBoxes(boxes => [...boxes, newBox])
    }

    const removeBox = idx => {
        const boxesCopy = [...boxes];
        setBoxes(boxes => boxesCopy.splice(idx,1))
    }

    return (
        <div className="BoxList">
            <NewBoxForm addBox={addBox} />
            {boxes.map((box, idx) => (<Box style={box} remove={() => removeBox(idx)} testId={idx}/>))}
        </div>
    )
}

export default BoxList;
