import React, { useState } from "react";

function Todo({task, remove, edit, testId, completed}){

    return (
        <>
        <div className={()=>{completed === true ? "completed" : "not-completed"}} data-testid={testId}>{task}</div>
        <button onClick={edit}>Edit</button>
        <button onClick={remove}>X</button>
        </>
    )
}

export default Todo;
